//import logo from './logo.svg'
import './App.css'

import React, {useState} from 'react'

function App() {

 // State to hold notes and the new note input
  const [notes, setNotes] = useState([])
  const [newNote, setNewNote] = useState("")

  const loadNotes = () => {
    const storedNotes = localStorage.getItem('notes')

    if (storedNotes) {
      setNotes(JSON.parse(storedNotes))
    }
  }

  const saveNotes = () => {
    localStorage.setItem('notes', JSON.stringify(notes))
  }

  const handleChange = (e) => {
    setNewNote(e.target.value)
  }

  const handleAddNote = () => {
    if (newNote.trim()){
      const updatedNotes = [...notes, newNote]
      setNotes(updatedNotes)
      setNewNote("")
    }
  }

  const handleDeleteNote = (index) => {
    const updateNotes = notes.filter((note, i) => i !== index)
    setNotes(updateNotes)
  }

  function addNote() {
    if (newNote !== "") {
      setNotes(notes.concat(newNote))
      setNewNote("")
    }
  }

  function deleteNote(index) {
    const updateNotes = notes.filter((notes, i) => i !== index)
    setNotes(updateNotes)
  }

  return (

    <div className="App">
      <h1>Notes App</h1>

      <input onChange={(e) => setNewNote(e.target.value)} type="text" value={newNote} placeholder="Enter a new note"/>

      <button onClick={addNote}>Add Note</button>

      <ul>
          {notes.map((note, index) => (
            <li key={index}>
              {note} 
              <button onClick={() => deleteNote(index)}>Delete</button>
            </li>
          ))}
      </ul>
    </div>
  )

}

export default App
